$(document).ready(function() {

	$('.headerLink').hover(function() {

		var text = $(this).find('img').attr('title');
		$('.headerLinkSubtitle').html(text);
		var newImage = $(this).find('img').attr('data-hover');
		var sourceImage = $(this).find('img').attr('src');
		$(this).find('img').attr('src', newImage);
		$(this).find('img').attr('data-hover', sourceImage);

	}, function() {

		$('.headerLinkSubtitle').html('');
		var newImage = $(this).find('img').attr('data-hover');
		var sourceImage = $(this).find('img').attr('src');
		$(this).find('img').attr('src', newImage);
		$(this).find('img').attr('data-hover', sourceImage);

	});

	$('.searchTerm').focus(function() {

		$('.socialIcons').fadeOut(function() {
			$('.searchTerm').animate({ width: 220 });
		});

	});

	$('.searchTerm').blur(function() {

		$('.searchTerm').animate({ width: 105 }, function() {
			$('.socialIcons').fadeIn();
		});

	});

});

	$(document).ready(function() {


		// add the extra forward slash to the artist names on an exhibition page
		$(".artistNames .artistName").append(" / ");
		var lastArtistName = $(".artistNames .artistName:last-child").html();
		if(lastArtistName !== null) {
			lastArtistName = lastArtistName.replace(" / ", "");
			$(".artistNames .artistName:last-child").html(lastArtistName);
		}


		/**
		 *
		 *	Controls the Show more content slider on an exhibition page
		 *
		 */
		var moreContentIsShowing = false;
		var moreContentHeight = $('#exhibitionReadMore').height();
		$('#exhibitionReadMore .contentLayoutWrapper, .exhibitionReadMoreContent').addClass('hidden');

		$('#exhibitionReadMore').prepend('

Find Out More
 ');

		$('.findOutMoreButtonContainer').bind("click", function() {
			if(moreContentIsShowing == false) {
				$('.findOutMoreButton').fadeOut(function() {
					$('#exhibitionReadMore .contentLayoutWrapper').animate({ height: moreContentHeight }, function() {
						$('.exhibitionReadMoreContent').fadeIn(function() {
							$('.findOutMoreButton').html("Show Less").fadeIn();
						});
					});
				});
				moreContentIsShowing = true;
			} else {
				$('.findOutMoreButton').fadeOut(function() {
					$('.exhibitionReadMoreContent').fadeOut(function() {
						$('#exhibitionReadMore .contentLayoutWrapper').animate({ height: 0 }, function() {
							$('.findOutMoreButton').html("Find Out More").fadeIn();
						});
					});
				});
				moreContentIsShowing = false;
			}
		});


		var subnavRowIsShowing = false;
		var subnavRow = $('#subnavRow');
		var subnav = $('#subnav');
		var currentlyShowingSubnav = $('.currentlyShowingSubnav');
		var currentlyShowingSubnavInList = $('.current_nav_item').parent();

		var positions = new Array();
		positions['subnav_whats_on'] = '-0px';
		positions['subnav_visit'] = '-260px';
		positions['subnav_learning'] = '-520px';
		positions['subnav_digital'] = '-780px';
		positions['subnav_food_drink'] = '-1040px';
		positions['subnav_shop'] = '-1300px';
		positions['subnav_about'] = '-1560px';
		positions['subnav_hire'] = '-1820px';
		positions['subnav_support'] = '-2080px';
		positions['subnav_39'] = '-2340px';


		fadeNavOut = function(from) {

			$('.primaryNavItem').removeClass('showingSubnav');

			var target = $('#' + $(from).attr('data-page-id'));

			$('#primaryNav').data('hover', true);

			displaySubNavContent(target);

			// show the subnav
			if(subnavRowIsShowing == false) {
				subnavRow.animate({ height : '260px' }, function() {
					$('#subNavRowContainer').fadeIn();
				});
				subnavRowIsShowing = true;
			}

			// move the arrow
			moveDownArrow($(from));

			return false;

		}



		navHoverConfig = {

			sensitivity: 5,
			interval: 100,
			timeout: 1000,
			over: function() {
				fadeNavOut(this);
			},
			out: function() {

				if($('#primaryNav').data('hover') == false) {

					$('#subNavArrowContainer').find('img').fadeOut();

					subnavRow.animate({ height : '0px' }, function() {
						$('#subNavRowContainer').fadeOut();
					});

					subnavRowIsShowing = false;

				}

			}

		}


		subnavHoverConfig = {

			sensitivity: 5,
			interval: 100,
			timeout: 300,
			over: function() {
			},
			out: function() {
				if($('#primaryNav').data('hover') == true) {
					fadeNavOut(this);
				}
			}

		}

		$('.subnavContainer').hover(function() {
			$('#primaryNav').data('hover', true);
		},function() {
			$('#primaryNav').data('hover', false);
		});

		$('#primaryNav').hover(function() {
			$('#primaryNav').data('hover', true);
		},function() {
			$('#primaryNav').data('hover', false);
		});

		$('.primaryNavItem').hoverIntent(navHoverConfig);
		$('#subnavRow').hoverIntent(subnavHoverConfig);


		moveDownArrow = function(target) {

			if(target.html() != null) {
				target.addClass('showingSubnav');
				var navOffset = target.offset();
				var containerOffset = $('#primaryNavHolder').offset();
				var halfWidth = (target.find('a').width()) / 2;
				var leftOffset = (navOffset.left - containerOffset.left) + halfWidth - 8;
				$('#subNavArrowContainer').animate({'left' : leftOffset});
			}

		}

		displaySubNavContent = function(target) {

			// if we've clicked on the menu item we're already on, ignore the click
			if(currentlyShowingSubnav.attr('id') != target.attr('id') || subnavRowIsShowing == false) {

				var scrollTo = positions[target.attr('id')];

				if(subnavRowIsShowing == false) {

					// the subnav is hidden so we go straight to the correct subnav
					$('#subNavContainerWrapper').css({ top : scrollTo });

				} else {

					// the subnav is showing so we animate the move
					$('#subNavContainerWrapper').animate({ top : scrollTo });

				}

				// add the down arrow
				$('#subNavArrowContainer').find('img').fadeIn();

				currentlyShowingSubnav = target;

			}

			if(target.attr('id') == undefined) {
				$('#subNavArrowContainer').find('img').fadeOut();
			}

			return false;

		};

		$('.subNavContainerClose').bind('click', function() {

/*
			$('#subNavArrowContainer').find('img').fadeOut();

			subnavRow.animate({ height : '0px' }, function() {
				$('#subNavRowContainer').fadeOut();
			});

			subnavRowIsShowing = false;

*/
			if($('#primaryNav').data('hover') == true) {



				$('#subNavArrowContainer').find('img').fadeOut();

				subnavRow.animate({ height : '0px' }, function() {
					$('#subNavRowContainer').fadeOut();
				});

				$('#primaryNav').data('hover', false);
				subnavRowIsShowing = false;

			}

			return false;

		});

		moveDownArrow(currentlyShowingSubnavInList);

		displaySubNavContent($('#' + currentlyShowingSubnavInList.parent().attr('data-page-id')));

		// force the proper nav items to show
		$('.primaryNavItem').removeClass('showingSubnav');

		var target = $('#' + $($('a.current_nav_item').parent()).attr('data-page-id'));

	});

	$(document).ready(function() {

		function calendar(targetContainer, params) {

			var now			= new Date();
			var thismonth	= now.getMonth();
			var thisyear	= now.getFullYear();

			// when we're loading content, we need to be able to disable clicks etc
			var loadingContent = false;

			var opts = {
				month: 5,
				year: 2012			};

			$.extend(opts, params);

			var monthNames = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'June', 'July', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
			var monthLongNames = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
			var dayNames = ['M', 'T', 'W', 'T', 'F', 'S', 'S'];
			month = i = parseInt(opts.month);
			year = parseInt(opts.year);
			var m = 0;

			var calendarContainer = '';

			// header
			calendarContainer += ('
');
			calendarContainer += ('
<');
			calendarContainer += ('
' + monthLongNames[month] + ' ' + year + '');
			calendarContainer += ('
>');
			calendarContainer += ('
');

			// day labels
			calendarContainer += ('
');
			for (d = 0; d < 7; d++) {
				divClass = "dayLabel";
				if(d == 6) divClass += (" last");
				calendarContainer += '
' + dayNames[d] + '';
			}

			calendarContainer += '
';

			var days			= getDaysInMonth(month, year);
			var firstDayDate	= new Date(year, month, 1);
			var firstDay		= firstDayDate.getDay();

			var prev_days		= getDaysInMonth(month, year);
			var firstDayDate	= new Date(year, month, 1);
			var firstDay		= firstDayDate.getDay();

			var prev_m = month == 0 ? 11 : month-1;
			var prev_y = prev_m == 11 ? year - 1 : year;
			var prev_days = getDaysInMonth(prev_m, prev_y);

			firstDay = (firstDay == 0 && firstDayDate) ? 7 : firstDay;
			firstDay = firstDay - 1;

			var i = 0;
	        for(j = 0; j < 42; j++) {

				divClass = "";
				if(j % 7 == 6) divClass += ('last ');

				if ((j < firstDay)) {

					//	Last month
					divClass += ('other_month_day');
					calendarContainer += ('
 ');

				} else if ((j >= firstDay + getDaysInMonth(month,year))) {

					//	Last month
					divClass += ('other_month_day');
					calendarContainer += ('
 ');

				} else {

					//	This month
					display_day = normalise_date(j - firstDay + 1);
					display_month = normalise_date(month + 1);
					id = year + '-' + display_month + '-' + display_day;
					display = "" + (j - firstDay + 1);
					if(display.length == 1) display = "0" + display;
					divClass += ('current_month_day ' + display);

					formattedDate = display + ' ' + monthLongNames[month] + ' ' + year;

					calendarContainer += ('
' + display + '');

				}

				if(j % 7 == 6) calendarContainer += ('
');

			}

			// add a holder for the listing content
			calendarContainer += ('








View full listings for this day');

			targetContainer.html(calendarContainer);

			// a user clicks on a day, let's show them what's on
			$('.current_month_day').click(function() {


				// make sure we're not already loading content (and that this is not a duplicate click)
				if(loadingContent == false) {

					loadingContent = true;

					// we want to scroll the page to the top of the calendar so the results can be seen properly
					$('html,body').animate({
						scrollTop: $(targetContainer).offset().top
					}, {
						duration: 1000,
						easing: 'swing'
					});
					targetDay = $(this).attr('rel');
					targetDayFormatted = $(this).attr('data-formattedDate');

					highlightDay(targetDay);
					cellDisplayHTML = $('[rel=' + targetDay + ']').html();
					showActivityIndicatorOnDay(targetDay);

					$.getJSON(
						'http://www.balticmill.com/baltic/getEventsOnDay/' + targetDay,
						function(data) {

							if(data.status == 200) {

								$('.listingContentOuterContainer').fadeIn(function() {
									$('.daysEvents').slideUp(function() {
										$('.dayTitleSpan').fadeOut(function() {
											// we now have no content showing - let's set the new content and bring it back!
											$('.dayTitleSpan').html(data.content.formattedDate).fadeIn(function() {
												$('.daysEvents').html(data.content.html).slideDown(function() {
													initHovers();
													// there is a "scrollSpace" on the right hand side of the event list that we want to match
													// the height of the events container for.
													$('.scrollSpace').animate({ height: ($('.eventsWrapper').height() - 4) });	
													loadingContent = false;
													removeActivityIndicatorOnDay(targetDay, cellDisplayHTML);
												});
											});
										});
									});
								});

							} else {

								alert("Sorry, there was a problem loading the calendar");

							}

						}
					);

				}


			});


			/**
			 *
			 *	Once the calendar is set up, we want to listen for clicks on the page.
			 *	If the click event occurs inside the calendar wrapper, an action is being taken on the calendar and we
			 *	let it complete. If the click occurs elsewhere on the page, we want to hide the calendar from view.
			 *
			 */			
			$('html').click(function() {
				closeCalendarEventList();
			});

			targetContainer.click(function(event) {
				// this prevents closeCalendarEventList() being called
				event.stopPropagation();
			});

			// the user wants to see next month
			$('.nextMonth').click(function() {
				window.location = $(this).attr('href');
				window.reload();
			});

			// the user wants to see next month
			$('.previousMonth').click(function() {
				window.location = $(this).attr('href');
				window.reload();
			});

			// the user wants to see full day listings
			$('.viewFullDayListings').click(function() {
				window.location = "http://www.balticmill.com/whats-on/calendar?date=" + targetDay;
				window.reload();
			});

		}


		/**
		 *
		 *	Closes the main event list dropdown shown underneath the calendar
		 *
		 */
		function closeCalendarEventList() {
			$('.daysEvents').slideUp(function() {
				$('.dayTitleSpan').fadeOut();
				$('.listingContentOuterContainer').fadeOut();
			});
		}


		function highlightDay(targetDay) {
			$('.highlighted').removeClass('highlighted');
			$('[rel=' + targetDay + ']').addClass('highlighted');
		}

		function showActivityIndicatorOnDay(targetDay) {
			$('[rel=' + targetDay + ']').html('');
			$('[rel=' + targetDay + ']').activity({segments: 12, steps: 1, opacity: 0.3, width: 2, space: 2, length: 4, color: '#5F5F5F', speed: 1, padding: 2});
		}

		function removeActivityIndicatorOnDay(targetDay, display) {
			$('[rel=' + targetDay + ']').html(display);
		}



		function normalise_date(input) {
			if(input < 10) input = '0' + input;
			return input;
		}

		function getDaysInMonth(month, year) {
			var daysInMonth= [31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31];
			if((month == 1) && (year % 4 == 0) && ((year % 100 != 0) || (year % 400 == 0))) {
				return 29;
			} else {
				return daysInMonth[month];
			}
		}


		// Set up and initialise the calendar
		$.fn.calendar = function(params) {
			calendar(this, params);
			return this;
		};
		$("#calendar").calendar();

		highlightDay('2012-06-07');

		function initHovers() {
			$('.eventImage').hover(function() {
				$(this).parent().find('.eventInformation').addClass('hover');
			}, function() {
				$(this).parent().find('.eventInformation').removeClass('hover');
			});
		}


	});

