link.script:

(function($){
    $.fn.extend({ 
        //plugin name - animatemenu
        setBodyScale: function(options) {

            var defaults = {
                scaleFactor: 0.3,
                maxScale: 210,
                minScale: 30
            };

			var obj = $('.text_wrap').width();

            var options = $.extend(defaults, options);

            return this.each(function() {
                  var o =options;



                  var fontSize = obj * o.scaleFactor; //Multiply the width of the body by the scaling factor:

	  if (fontSize > o.maxScale) fontSize = o.maxScale;
      if (fontSize < o.minScale) fontSize = o.minScale; //Enforce the minimum and maximums
	  $(this).css('font-size', fontSize + '%');           


            });
        }
    });
})(jQuery);

$( document ).ready( function($) {
	//$('#menu').animateMenu({animatePadding: 30, defaultPadding:10});

	$('.news_title').setBodyScale({minScale:150});
	$('.news_sub').setBodyScale({maxScale:78, scaleFactor: 0.12, minScale:60});

	$(window).resize(function(){
                $('.news_title').setBodyScale({minScale:150});
				$('.news_sub').setBodyScale({maxScale:78, scaleFactor: 0.12, minScale:60});
            });

            //Fire it when the page first loads:
            //setBodyScale('.news_title', '210');
        });

