jQuery.fn.popularDisqus = function(){
jQuery.ajax({
url: "/?event=page.returnMostCommentedArticles.ajax&nNumberToDisplay=6",
success: function( data ) {
$('#mostCommentedContent').html(data);
}
});
};
$(document).ready(function(){
$.fn.popularDisqus();
});

