if (typeof gomez != 'undefined') { 
	var AccountID = gomez['acctId']; 
	var MyPageID = gomez['pgId']; 

	if(navigator.userAgent.indexOf('Gomez')!=-1) { 
		AccountID = 'E894B5'; 
		MyPageID = MyPageID +'_Gomez'; 
		gomez['pgId'] = MyPageID; 
		gomez['acctId'] = AccountID; 
	} 
	else if (navigator.userAgent.indexOf('Catchpoint')!=-1) { 
		AccountID = 'E894B5'; 
		MyPageID = MyPageID +'_Catchpoint'; 
		gomez['pgId'] = MyPageID; 
		gomez['acctId'] = AccountID;
	}; 
	if (gomez['pgId'] == 'http://www.overstock.com/Home-Garden/1/store.html') { 
                var tempId = ( typeof tempPageId != 'undefined' ) ? tempPageId : 'HomeGarden1';
		AccountID = 'E894B5'; 
		gomez['acctId'] = AccountID; 
                MyPageId = tempId;
		gomez['pgId'] = MyPageID; 
	};
}

