div.script:
var MP = { /* mp_trans_disable_start */ Version: '1.0.22',
Domains: {'es':'www.overstock.com'}, SrcLang: 'en', /* mp_trans_disable_end */ UrlLang: 'mp_js_current_lang',
SrcUrl: unescape('mp_js_orgin_url'), /* mp_trans_disable_start */ init: function() {
	if(MP.UrlLang.indexOf('p_js_') == 1) {
		MP.SrcUrl = window.top.document.location.href;
		MP.UrlLang = MP.SrcLang;
	}
}, getCookie: function(name) {
	var start = document.cookie.indexOf(name + '=');
	if(start < 0)
		return null;
	start = start + name.length + 1;
	var end = document.cookie.indexOf(';', start);
	if(end < 0)
		end = document.cookie.length;
	while(document.cookie.charAt(start) == ' ') {
		start++;
	}
	return unescape(document.cookie.substring(start, end));
}, setCookie: function(name,value,path,domain) {
	var cookie = name + '=' + escape(value);
	if(path)
		cookie += '; path=' + path;
	if(domain)
		cookie += '; domain=' + domain;
	var now = new Date();
	now.setTime(now.getTime() + 1000 * 60 * 60 * 24 * 365);
	cookie += '; expires=' + now.toUTCString();
	document.cookie = cookie;
}, switchLanguage: function(lang) {
	if(lang != MP.SrcLang) {
		var script = document.createElement('SCRIPT');
		script.src = location.protocol + '//' + MP.Domains[lang] + '/' + MP.SrcLang + lang + '/?1023749632;' + encodeURIComponent(MP.SrcUrl);
		document.body.appendChild(script);
	} else if(lang == MP.SrcLang && MP.UrlLang != MP.SrcLang) {
		var script = document.createElement('SCRIPT');
		script.src = location.protocol + '//' + MP.Domains[MP.UrlLang] + '/' + MP.SrcLang + MP.UrlLang + '/?1023749634;' + encodeURIComponent(location.href);
		document.body.appendChild(script);
	}
	return false;
}, switchToLang: function(url) {
	window.top.location.href = url;
}/* mp_trans_disable_end */
};

a.onclick:
return switchLanguage('es');
		function switchLanguage(lang) {
		MP.SrcUrl=unescape('mp_js_orgin_url');MP.UrlLang='mp_js_current_lang';MP.init();
		MP.switchLanguage(MP.UrlLang==lang?'en':lang);
		return false;
		}

link.script:
    if (!os) var os = {};
    os.Paths = {
        CDN_PATH: 'http://ak1.ostkcdn.com/'
    };

    //Header vars
    var IsLogged = false;
var IsGuest = false;
    os.UserSettings = {
        itemsInCart: 0,
        firstName: 'Valued',
        lastName: 'Customer',
        cluboEndDate: '',
        cluboCurrentRewards: '',
        isLogged: IsLogged || false,
        getCluboMsg: function(){
        	var cluboMsgReturn = "";
            if (new Date(this.cluboEndDate) > new Date()) {
             	cluboMsgReturn = "Your Club O Dollars: " +  this.cluboCurrentRewards + "";
                // Calculate the number of days between now and the club o expiration date. If less than 60 days until expiration, then show the expiration date. (FED-2356)
                var today = new Date();
                var expDate = new Date(this.cluboEndDate);
                
                function days_between(date1, date2) {
                	var ONE_DAY = 1000 * 60 * 60 * 24
                    var date1_ms = date1.getTime()
                    var date2_ms = date2.getTime()
                    var difference_ms = Math.abs(date1_ms - date2_ms)
                    return Math.round(difference_ms/ONE_DAY)
                }
                var expirationDelta = days_between(today,expDate); 
                if (expirationDelta <= 60) {
                	// Append the expiration message to the rewards balance message.
                    cluboMsgReturn += " Membership Exp: " + this.cluboEndDate + "";
                }             
            } else {
             cluboMsgReturn = "Your Club O membership has expired. Click here to renew.";
            }
            return cluboMsgReturn;
        },
        getWelcomeMsg: function(){
            return (this.isLogged && this.firstName != "Valued") ? "Hi, " + this.firstName + ". Let's Start Saving." : "Let's Start Saving.";   
        }
    };

