            function getUrlVars(){

                var vars = [], 
                    hash,
                    hrefVal = window.location.href;
                var hashes = hrefVal.slice(hrefVal.indexOf('?') + 1).split('&');
                for(var i = 0, d = hashes.length; i < d; i++)
                {
                  hash = hashes[i].split('=');
                  vars.push(hash[0]);
                  vars[hash[0]] = hash[1];
                }
                return vars;
            }
            function getUrlVar(name){
                return getUrlVars()[name];
            }

