body.script:
if((navigator.userAgent.match(/iPhone/i) || (navigator.userAgent.toLowerCase().search("android") > -1) && (navigator.userAgent.toLowerCase().search("mobile") > -1))) {
var dateNow = new Date();
if(get_cookie("sunAppDuration")){
var dateThen = new Date(get_cookie("sunAppDuration"));
if(days_between(dateThen,dateNow) < 60){
//the 1st seen date is within the duration
if (get_cookie("sunAppFrequency")){
//Do Nothing
} else {
set_cookie ("sunAppFrequency", "true", 60 * 60 * 24 * 7, "" );
showAppPopup();
} 
}
}else{
//record the 1st popup datetime
set_cookie ("sunAppDuration", dateNow.toUTCString(),3153600000, "" );//100 years 
set_cookie ("sunAppFrequency", "true", 60 * 60 * 24 * 7, "" );
showAppPopup(); 
}
}
function days_between(date1, date2) {
// The number of milliseconds in one day
var ONE_DAY = 1000 * 60 * 60 * 24;
// Convert both dates to milliseconds
var date1_ms = date1.getTime();
var date2_ms = date2.getTime();
// Calculate the difference in milliseconds
var difference_ms = Math.abs(date1_ms - date2_ms);
// Convert back to days and return
return Math.round(difference_ms/ONE_DAY);
} 
function showAppPopup() {
if(navigator.userAgent.match(/iPhone/i)) {
var r=confirm("Download our app to view the latest stories optimised for your phone.");
if (r==true)
{
window.location = "http://itunes.apple.com/gb/app/the-sun-for-iphone/id485672520?mt=8";
}
} 
var userAgent = navigator.userAgent.toLowerCase();
if ((userAgent.search("android") > -1) && (userAgent.search("mobile") > -1)){
var r=confirm("Download our app to view the latest stories optimised for your phone.");
if (r==true)
{
window.location = "https://market.android.com/details?id=uk.co.thesun&feature=search_result#?t=W251bGwsMSwxLDEsInVrLmNvLnRoZXN1biJd";
}
} 
}
