function mobileAppAlert() {

    if(CORE_SITE_IDS.indexOf(SITE_ID) == -1) return;

    if(!IS_GUEST) return;

    var mobileAppAlertCookie = $.cookie('mobileAppAlert');
    if(mobileAppAlertCookie) return;



    if((navigator.userAgent.match(/iPhone/i))||(navigator.userAgent.match(/iPod/i))) {
        var question = confirm("Flocasts has an app for iPhone and IPod Touch! Click OK to learn more about Flosports and Be Awesome!");
        if (question) {
            window.location = "http://itunes.apple.com/us/app/flosports/id506486216";
        }else{

        }

    }
    else if((navigator.userAgent.match(/android/i))) {
        var androidversion = parseFloat(navigator.userAgent.slice(navigator.userAgent.indexOf("Android")+8));     
        if(androidversion <= 2.1 || androidversion >= 4) return;

        if(!navigator.userAgent.match(/mobile/i)) return;

        var question = confirm("Flocasts has an app for Android! Click OK to learn more about Flosports and Be Awesome!");
        if (question) {
            window.location = "market://details?id=com.flocasts.Flosports";
        }else{

        }


    }

    $.cookie('mobileAppAlert', true);



 }

