div.script:
/* special thanks to http://adomas.org/javascript-mouse-wheel/ */
function moveUp() {
    var div = document.getElementById("scroll_content_holder");
    div.scrollTop = parseInt(div.scrollTop) - 5; // orig 2
    mu = setTimeout("moveUp()", 25);
}

function moveDown() {
    var div = document.getElementById("scroll_content_holder");
    div.scrollTop = parseInt(div.scrollTop) + 5; // orig 2
    md = setTimeout("moveDown()", 25);
}

function stopUp() {
    clearTimeout(mu);
}

function stopDown() {
    clearTimeout(md);
}

function handle(delta) {
	if (delta < 0) {
		moveDown();
		stopDown();
	} else {
		moveUp();
		stopUp()
	}
}

/** Event handler for mouse wheel event.
 */
function wheel(event){
        var delta = 0;
        if (!event) /* For IE. */
                event = window.event;
        if (event.wheelDelta) { /* IE/Opera. */
                delta = event.wheelDelta/120;
                /** In Opera 9, delta differs in sign as compared to IE.
                 */
                if (window.opera)
                        delta = -delta;
        } else if (event.detail) { /** Mozilla case. */
                /** In Mozilla, sign of delta is different than in IE.
                 * Also, delta is multiple of 3.
                 */
                delta = -event.detail/3;
        }
        /** If delta is nonzero, handle it.
         * Basically, delta is now positive if wheel was scrolled up,
         * and negative, if wheel was scrolled down.
         */
        if (delta)
                handle(delta);
        /** Prevent default actions caused by mouse wheel.
         * That might be ugly, but we handle scrolls somehow
         * anyway, so don't bother here..
         */
        if (event.preventDefault)
                event.preventDefault();
	event.returnValue = false;
}

function initScroll( event ) {
	/*
	 *  We hide the content only if the browser has javascript enabled. If the browser does not
	 * have js enabled he will see the whole content at once (the content will possible overlap other page elements)
	 */
	document.getElementById("scroll_content_holder").style.overflow = 'hidden';
}

/** Initialization code.
 * If you use your own event management code, change it as required.
 */
if (window.addEventListener) {
	/*  DOMMouseScroll for standard compliant browsers  */
	document.getElementById("scroll_content_holder").addEventListener('DOMMouseScroll', wheel, false);
	window.addEventListener("load", initScroll, false);
} else {
	/*  Non-standard compliat browsers (IE)  */
	document.getElementById("scroll_content_holder").onmousewheel = document.onmousewheel = wheel;
	window.attachEvent("onload", initScroll);
}
