function clicky_gc( name ) {
	var ca = document.cookie.split(';');
	for( var i in ca ) {
		if( ca[i].indexOf( name+'=' ) != -1 )
			return decodeURIComponent( ca[i].split('=')[1] );
	}
	return '';
}
