div.script:
function changeImg(back){
	if(back == true){
		var newImg = "cube_index_";
		document.getElementById('cube_11').style.backgroundImage = "url(/images/"+newImg+"11.jpg)";
		document.getElementById('cube_12').style.backgroundImage = "url(/images/"+newImg+"12.jpg)";
		document.getElementById('cube_13').style.backgroundImage = "url(/images/"+newImg+"13.jpg)";

		document.getElementById('cube_21').style.backgroundImage = "url(/images/"+newImg+"21.jpg)";
		document.getElementById('cube_22').style.backgroundImage = "url(/images/"+newImg+"22.jpg)";
		document.getElementById('cube_23').style.backgroundImage = "url(/images/"+newImg+"23.jpg)";

		document.getElementById('cube_31').style.backgroundImage = "url(/images/cubes.png)";
		document.getElementById('cube_32').style.backgroundImage = "url(/images/cubes.png)";
		document.getElementById('cube_33').style.backgroundImage = "url(/images/cubes.png)";
	} else {
		var newImg = "cube_indexHover_";
		for (var i=1; i < 4; i++) {
			document.getElementById('cube_'+i+'1').style.backgroundImage = "url(/images/"+newImg+""+i+"1.jpg)";
			document.getElementById('cube_'+i+'2').style.backgroundImage = "url(/images/"+newImg+""+i+"2.jpg)";
			if(i != 3){ 
				document.getElementById('cube_'+i+'3').style.backgroundImage = "url(/images/"+newImg+""+i+"3.jpg)";
			}

		}
	}
}
