function mtUpdateSignInWidget(u) {
    var separator = '&nbsp;&nbsp;&nbsp;&nbsp;';
    var el = document.getElementById('signin-widget-content');
    var content = '';
    if (!el) return;
    if (u) {
        if (u && u.is_authenticated) {
            user = u;
            mtSaveUser();
        } else {
            // user really isn't logged in; so let's do this!
            return mtSignIn();
        }
    } else {
        u = mtGetUser();
    }


    if (!u) {
        mtFetchUser();
        u = mtGetUser();
    }

    if (u && u.name) {
        var url;
        if (u.is_authenticated) {
            if (u.is_author) {
                url = 'http://www.kcet.org/cgi-bin/mt/mt-cp.cgi?__mode=view&blog_id=68';
                url += '&static=' + encodeURIComponent( location.href );
            } else {
                url = u.url;
            }
        } else if (u.url) {
            url = u.url;
        } else {
            url = null;
        }
        if (url)
        {   /*content += 'Welcome,&nbsp;&nbsp;<a href="' + url + '">' + u.name + '</a>';*/
            content += 'Welcome,&nbsp;&nbsp;' + u.name + '';
            content += separator + separator + '<a href="' + url + '" class="profilelink">View Profile</a>';}
        else{
            content += 'Welcome,&nbsp;&nbsp;' + u.name + '';}
        if (u.is_author)
            content += separator + '<a href="http://www.kcet.org/cgi-bin/mt/mt-cp.cgi?__mode=edit&return_to=' + encodeURIComponent(document.URL) + '" class="profilelink">Edit Profile</a>';




       	content += separator + '<a href="javascript:void(0)" onclick=" return mtSignOutOnClick()" class="profilelink">Sign Out</a>';

    } else if (u && u.is_banned) {
        content = 'You do not have permission to sign in to this blog.';
    } else {
        content = '<a href="javascript:void(0)" onclick="return mtSignInOnClick(\'signin-widget-content\')">Sign In</a>';


        content += separator + '<a href="http://www.kcet.org/cgi-bin/mt/mt-cp.cgi?__mode=register&blog_id=68&return_to=' + encodeURIComponent(document.URL) + '">Register</a>';


    }
    el.innerHTML = content;
}
mtAttachEvent('usersignin', mtUpdateSignInWidget);
mtUpdateSignInWidget();
