/* Catch any potential errors to keep the rest of the page from breaking */
try {
	var omInternalDomains = ['visitorlando.com'];
	
	/*
	 * Track a click of some sort:
	 * 		linkURL:	Required - The URL for the link
	 * 		linkName:	The name for the link. If not specified, set to the URL for the link
	 * 		linkType:	The type of link. Can be one of:
	 * 					e - External
	 * 					d - File Download
	 * 					o - Custom Links
	 * 					
	 * 					If not specified, o is the default.
	 * 		object:		The HTML object clicked on. This is not required.
	 * 		events:		The Omniture Events that need to be sent. Comma-seperated list without spaces.
	 * 		eVars:		The Omniture eVars that need to be sent. Comma-seperated list without spaces.
	 * 		eVarValues:	The values for the eVars. Comma-seperated list without spaces. Must be in
	 * 					the same order as the eVars.
	 */
	function omTrackClick(linkURL, linkName, linkType, object, events, eVars, eVarValues) {
		var s = s_gi('orlandovisitorlando');
		
		if (typeof linkURL === 'undefined' || linkURL === null) {
			return false;
		}
		if (typeof linkName === 'undefined' || linkName === null) {
			linkName = linkURL;
		}
		if (typeof linkType === 'undefined' || linkType === null) {
			linkType = 'o';
		}
		if (typeof object === 'undefined' || object === null) {
			object = true;
		}
		if (typeof events === 'undefined' || events === null) {
			events = '';
		}
		if (typeof eVars === 'undefined' || eVars === null) {
			eVars = '';
		}
		if (typeof eVarValues === 'undefined' || eVarValues === null) {
			eVarValues = '';
		}
		
		if (eVars.length > 0 && eVarValues.length > 0) {
			var keys = eVars.split(',');
			var values = eVarValues.split(',');
			
			for (var i = 0; i < keys.length; i++) {
				if (typeof values[i] === 'string') {
					s[keys[i]] = values[i];
				}
			}
		}
		
		s.linkTrackVars = eVars;
		s.linkTrackEvents = s.events = events;
		
		if (events.length > 0) {
			s.linkTrackVars += ',events';
		}
		
		if (s.linkTrackVars.length === 0) {
			s.linkTrackVars = 'None';
		}
		if (s.linkTrackEvents.length === 0) {
			s.linkTrackEvents = 'None';
		}
		
		s.pageName = 'US | LISTINGS';
		
		var lt = linkURL != null ? s.lt(linkURL) : '';
		if (lt == '') {
			s.tl(object, linkType, linkName);
		}
	}
	
	var s_account = "orlandovisitorlando";
	var s = s_gi(s_account);
	/************************** CONFIG SECTION **************************/
	/* You may add or alter any code config here. */
	s.charSet = "UTF-8";
	/* Conversion Config */
	s.currencyCode = "USD";
	/* Link Tracking Config */
	s.trackDownloadLinks = false;
	s.trackExternalLinks = false;
	s.trackInlineStats = true;
	s.linkDownloadFileTypes = "exe,zip,wav,mp3,mov,mpg,avi,wmv,doc,pdf,xls";
	s.linkInternalFilters = "javascript:,.visitorlando.com,visitorlando.com,tickets.visitorlando.com,conventiontickets.visitorlando.com";
	s.linkLeaveQueryString = false;
	s.linkTrackVars = true;
	s.linkTrackEvents = true;
	//s.Media.autoTrack = true;
	
