	$(document).ready(function() {
		/*
		 * There's a default onClick that's defined for the "Remove" button in the itinerary and that returns before
		 * our custom click for tracking can be run. So lets remove that and execute it (if it exists) after we do
		 * our own thing.
		 */
		$('.iconRemoveItin, .textRemoveItin').each(function() {
			var myOnClick = $(this).attr('onclick');
			$(this).removeAttr('onclick');
			
			$(this).click(function(event) {
				var linkID = $(this).attr('linkid');
				
				if (typeof linkID !== 'undefined' && linkID !== null && linkID.length > 0) {
					var s = s_gi('orlandovisitorlando');
					s.linkTrackVars = 'prop2,events';
					s.linkTrackEvents = 'event11';
					s.events = 'event11';
					s.prop2 = 'US | LISTINGS';
					var lt = $(this).attr('href') != null ? s.lt($(this).attr('href')) : '';
					if (lt == '') {
						s.tl(this, 'o', linkID);
					}
				}
				
				if (typeof myOnClick !== 'undefined' && myOnClick !== null) {
					return myOnClick(this);
				}
			});
		});
		
		$('a').live('click', function() {
			var linkType = 'e';
			var events = '';
			var linkName = '';
			var eVars = [];
			var eVarValues = [];
			var $obj = $(this);
			var obj = this;
			
			// demo links
			// 	
			// 			
			
			// site content -> links -> custom links
			if ($obj.filter('a.extLinkTrack, a.linkOut2').length) {
				events = 'event7';
				eVars.push('prop2');
				eVarValues.push('US | LISTINGS');
				
				linkName = $(this).attr('linkid');
				
				if (typeof linkName == 'undefined' || linkName == null || linkName.length == 0) {
				
					linkName = 'UNKNOWN | ';
					if (typeof $obj.attr('title') === 'string' && $.trim($obj.attr('title')).length > 0) {
						linkName = $obj.attr('title').toUpperCase() + ' | ';
					} else if (typeof $obj.attr('alt') === 'string' && $.trim($obj.attr('alt')).length > 0) {
						linkName = $obj.attr('alt').toUpperCase() + ' | ';
					}
					linkName += $obj.attr('href');
					
				}
				
				linkType = 'e';
			} else {
				if (/(\.exe|\.zip|\.wav|\.mp3|\.mov|\.mpg|\.avi|\.wmv|\.docx?|\.pdf|\.xlsx?)$/i.test(obj.href)) {
					/*
					 * File Download Link Tracking
					 */
					events = 'event8';
					eVars.push('prop2');
					eVarValues.push('US | LISTINGS');
					
					linkType = 'd';
					linkName = $obj.attr('title').length ? $obj.attr('title') : $obj.attr('alt');
					
					if (typeof linkName !== 'string' || linkName.length <= 0) {
						linkName = obj.href.replace(/^([A-Z]+:\/\/)?.*\//i, '');
					}
					linkName = 'FILE DOWNLOAD: ' + linkName;
				} else if ($obj.filter('a.printOffer').length) {
					events = 'event9';
					eVars.push('prop2');
					eVarValues.push('US | LISTINGS');
					
					linkType = 'o';
					linkName = $(this).attr('refid');
					
					if (typeof linkName !== 'string' || linkName.length <= 0) {
						linkName = 'UNKNOWN';
					}
					linkName = 'PRINTED OFFER|' + linkName;
				} else if ($obj.filter('a.iconAddItin, a.textAddItin').length) {
					var linkName = $(this).attr('linkid');
					
					if (typeof linkName !== 'string' || linkName.length === 0) {
						return true;
					}
					events = 'event10';
					eVars.push('prop2');
					eVarValues.push('US | LISTINGS');
					
					linkType = 'o';
				} else if (typeof $obj.attr('href') !== 'string') {
					/*
					 * Not an external URL. Don't track this.
					 */
					return true;
				} else {
					/*
					 * Might be an external URL, check it against the internal domains list...
					 */
					for (var i = 0; i < omInternalDomains.length; i++) {
						var regEx = new RegExp(omInternalDomains[i], 'i');
						if ($obj.attr('href').search(regEx) !== -1) {
							return true;
						}
					}
					
					events = 'event7';
					eVars.push('prop2');
					eVarValues.push('US | LISTINGS');
					
					linkName = 'UNKNOWN | ';
					if (typeof $obj.attr('title') !== 'undefined' && $.trim($obj.attr('title')).length > 0) {
						linkName = $obj.attr('title').toUpperCase() + ' - ';
					} else if (typeof $obj.attr('alt') !== 'undefined' && $.trim($obj.attr('alt')).length > 0) {
						linkName = $obj.attr('alt').toUpperCase() + ' - ';
					}
					linkName += $obj.attr('href');
					linkType = 'e';
				}
			}
			
			omTrackClick($obj.attr('href'), linkName, linkType, this, events, eVars.join(','), eVarValues.join(','));
			
			return true;
		});
	});
	
	/************* DO NOT ALTER ANYTHING BELOW THIS LINE ! **************/
	var s_code=s.t();if(s_code)document.write(s_code);
} catch(err) {
	if (typeof console === 'undefined' || console === null) {
