head.script:
//
			GujAd.getClient().setIsIndexPage(true);

	GujAd.registerAdPositionLoadedHandler(function(position) {
		if (position.getName() == 'superbanner_1' && (true || position.getAdType() == 'wallpaper')) {
			moveSuperbannerToWallpaperSlot();
		}
	}, true);

	function moveSuperbannerToWallpaperSlot() {
		var superbannerDiv = document.getElementById('superbanner_1');
		var wallpaperDiv = document.getElementById('wallpaper');
		var firstHtmlElement;

		while (firstHtmlElement = superbannerDiv.firstChild) {
			// skip script tags. otherwise they will be executed.
			if (firstHtmlElement.tagName && firstHtmlElement.tagName.toLowerCase() == "script") {
				superbannerDiv.removeChild(firstHtmlElement);
			} else { // move html
				wallpaperDiv.appendChild(firstHtmlElement);
			}
		}

		superbannerDiv.style.display = 'none';
		$('#wp-container').css('height', '175px');
		$('#wallpaper').show();

		$('#ausgabeNaviContainer').css('top', '217px');
	}

	if (window.location.search.indexOf('gujad=1' == true)) {
		document.write('<scr'+'ipt type="text/javascript" src="http://test.adtech2.guj.de/satellite.php/get-file-content/1/pm-magazin.de/gujAd.js"></scr'+'ipt>');
		document.write('<link rel="stylesheet" type="text/css" media="screen" href="http://test.adtech2.guj.de/satellite.php/get-file-content/1/pm-magazin.de/gujAd.css">');
	} else {
		document.write('<scr'+'ipt type="text/javascript" src="/gujAd/gujAd.js"></scr'+'ipt>');
		document.write('<link rel="stylesheet" type="text/css" media="screen" href="/gujAd/gujAd.css">');
	}
