div.script:
function updateBuehneTeaser() {
	var i = $(this).attr('item');
	$('.buehne_teaser').show();
	$('.buehne_teaser[item="' + i +'"]').hide();
}
$(document).ready(function() {
	$('.buehne_detail_container').cycle({
		after: updateBuehneTeaser
	});
});

