    <item>
      <title>PRO”s “Performance Rights Organizations” regulate Urban Radio</title>
      <link>http://africanamericanfamilyconnection.com/pros-regulate-urban-radio.htm</link>
      <comments>http://africanamericanfamilyconnection.com/pros-regulate-urban-radio.htm#comments</comments>
      <pubDate>Wed, 13 May 2009 01:30:21 +0000</pubDate>
      <dc:creator>LSlack</dc:creator>
      <category><![CDATA[entertainment]]></category>
      <category><![CDATA[aafc]]></category>
      <category><![CDATA[african american family connecton]]></category>
