      <category><![CDATA[Group Party Games]]></category>
      <category><![CDATA[Indoor Party Games]]></category>
      <category><![CDATA[Mystery Party Games]]></category>
      <category><![CDATA[Outdoor Party Games]]></category>
      <category><![CDATA[Party Games For Girls]]></category>
      <category><![CDATA[Princess Party Games]]></category>
