    <item>
      <guid isPermaLink="false">tag:blogger.com,1999:blog-312071380483701494.post-8281129787896504238</guid>
      <pubDate>Tue, 01 Jul 2008 04:34:00 +0000</pubDate>
      <atom:updated>2008-06-30T21:35:50.115-07:00</atom:updated>
      <category domain="http://www.blogger.com/atom/ns#">samsung ln52a650 review</category>
      <category domain="http://www.blogger.com/atom/ns#">samsung</category>
      <category domain="http://www.blogger.com/atom/ns#">lcd</category>
      <category domain="http://www.blogger.com/atom/ns#">hdtv</category>
      <category domain="http://www.blogger.com/atom/ns#">entertainment</category>
      <category domain="http://www.blogger.com/atom/ns#">tv</category>
