      <pubDate>Sun, 03 Apr 2011 15:16:35 +0000</pubDate>
      <dc:creator>Trains O Scale</dc:creator>
      <category><![CDATA[Recommended Resources]]></category>
      <category><![CDATA[model trains]]></category>
