//	RSStoHTML() rewritten to use local XML cron'ed over from CEN Day platform
function RSStoHTML(xmlURL, numItems, divID) {
    var xmlDoc;
    new Ajax.Request(xmlURL, {
        method: "get",
        asynchronous: false,
        onSuccess: function(transport) {
            var xml = transport.responseXML;
			xmlItems = xml.getElementsByTagName('item');
			var ffbUL = new Element('ul');
			for (i=0; (i<xmlItems.length && i<numItems); i++) {
				var xmlUrl = null;
				if (xmlItems[i].getElementsByTagName('link').length) xmlUrl = xmlItems[i].getElementsByTagName('link')[0].childNodes[0].nodeValue;
				var xmlTitle = null;
				if (xmlItems[i].getElementsByTagName('title').length) xmlTitle = xmlItems[i].getElementsByTagName('title')[0].childNodes[0].nodeValue;
				var xmlDate = null;
				if (xmlItems[i].getElementsByTagName('pubDate').length) xmlDate = xmlItems[i].getElementsByTagName('pubDate')[0].childNodes[0].nodeValue;
				var date = new Date(xmlDate);
				ffbUL.insert({
					'bottom': new Element('li').update(
						new Element('span', {'class': 'headline'}).update(
							new Element('a', {'href': xmlUrl}).update(xmlTitle)
						)
					).insert({
						'bottom': new Element('p', {'class': 'date'}).update(
							date.toDateString().replace(
								/\w{3} (.*) (\d{4})$/, "$1, $2"
							).replace(
								/^Jan\b/, 'January'
							).replace(
								/^Feb\b/, 'February'
							).replace(
								/^Mar\b/, 'March'
							).replace(
								/^Apr\b/, 'April'
							).replace(
								/^Jun\b/, 'June'
							).replace(
								/^Jul\b/, 'July'
							).replace(
								/^Aug\b/, 'August'
							).replace(
								/^Sep\b/, 'September'
							).replace(
								/^Oct\b/, 'October'
							).replace(
								/^Nov\b/, 'November'
							).replace(
								/^Dec\b/, 'December'
							)
						)
					})
				});
			}
            var ffb = $(divID).update(ffbUL);
        }
    });
}
//	END RSStoHTML() rewritten

//	Trial 25 User Progress Bar
	function advanceUserProgress(step) {
	$$('.user-progress-pane').each(Element.hide);
	$('user-progress-pane-' + step).show();
	for (var i = 1; i < $$('.user-progress-bar').length; i++) {
		if (i < step) $('user-progress-bar-' + i).addClassName('completed');
		$('user-progress-bar-' + i).removeClassName('active')
	}
	$('user-progress-bar-' + step).addClassName('completed active');
	return false;
}
//	End Trial 25 User Progress Bar

//	Stay Current Module Tracking
function stayCurrentAdTracking(url) {
	_gaq.push(['_trackEvent', 'Stay Current Module', 'click', url]);
	setTimeout(function(){this.location = url}, 100);
	return true;
}
//	End Stay Current Module Tracking

 document.observe('dom:loaded', function(e) {
 	//	Register Button link replacement for tracking
 	$$('#profile #registerButton').each(function(link) {
 		link.setAttribute('href', 'https://portal.acs.org/portal/acs/corg/memberapp?_nfpb=true&_pageLabel=mapp_register_page&cid=RegistrationApp_PubsStart');
 	});
 	//	End Register Button link replacement for tracking 	

	//	Sample Issue Subscription Link Replacement
	$$('#sampleNotice a').each(function(link) {
		link.setAttribute('href', link.href.replace(/\/page\/(\w+)\/subscribe.html$/, "/page/subscribe.html?ref=$1"));
	});	
	//	End Sample Issue Subscription Link Replacement

	//	EULA Fix for "Do not accept" button
 	if ($('eulaLayer')) {
 		$$('#eulaLayer a.rButton.bgRed')[0].onclick = function(e) {
 			$('eulaLayer').setStyle({'display': 'none'}); 
 			return false;
 		}
 	}
 	//	End EULA Fix for "Do not accept" button

 	//	Subscription Page journal masthead swap if #coden in path
	var subscriptionCoden = window.location.href.match(/subscribe.html\?ref\=([a-zA-Z0-9]+)$/);
	if (subscriptionCoden) {
		var coden = subscriptionCoden[1];
		new Ajax.Request(
			'/appl/literatum/publisher/achs/journals/covergifs/' + subscriptionCoden[1] + '/title.gif', {
				onSuccess: (function() {
					return function() {
						var logoLink = $$('#pubsLogo a');
						if (logoLink.length) {
							logoLink[0].href = '/journal/' + coden;
							logoLink[0].title = 'Journal Home Page';
							logoLink[0].select('img')[0].src = '/appl/literatum/publisher/achs/journals/covergifs/' + coden + '/title.gif';
						}
					}
				})()
			}
		);
	}
 	//	End Subscription Page journal masthead swap

	// Suppression of invalid Most Read articles in I&ECR and JC&ED
	if ('/action/showMostReadArticles' == this.location.pathname && this.location.search.match('journalCode=jceaax')) {
		var ghar = [
			'10.1021/je200019g',
			'10.1021/je200444f',
			'10.1021/je2001045',
			'10.1021/je101061t',
			'10.1021/je101190p'
		];
		$$('div.articleBox div.DOI').each(function(doi) {
			var d = doi.innerHTML.match(/10.1021\/.*$/);
			if (-1 !== ghar.indexOf(d[0])) doi.up('div.articleBox').hide();
		});
	}
	// END Suppression of invalid Most Read articles in I&ECR and JC&ED
 });

