/* Add this to live chat button click handler to fetch user email and auto-populate*/
// Get user's name and email from ERIGHTS cookie
function liveChatStartHandler() {
	var sessionRequest;
	var userEmailAddress = '';
	var cookieObject = {};
	var cookieArray = document.cookie.split('; ');
	for (var c = 0; c < cookieArray.length; c++) {
		cookieObject[cookieArray[c].split('=')[0]] = cookieArray[c].split('=')[1];
	}
	if (cookieObject.hasOwnProperty('ERIGHTS')) {
		var lookupURL = 'http://services.acs.org/acsid/LookupSession?session=' + cookieObject.ERIGHTS;
		var lookupRequest = new XMLHttpRequest();
		lookupRequest.open('GET', lookupURL);
		lookupRequest.onreadystatechange = function (aEvt) {
		    if (lookupRequest.readyState == 4) {
				var responseObject = JSON.parse(lookupRequest.responseText);
				if (responseObject.hasOwnProperty('email')) {
					userEmailAddress = responseObject.email;
				}
				SnapABug.startLink();
		    }
			SnapABug.setUserEmail(userEmailAddress);
		}
		lookupRequest.send();
	} else {
		SnapABug.startLink();
	}
	return false;
}

// Start diagnostics page sender

var snapAUserIdentity;
var snapASnoop;

function gotSnoopAndUserIdentities() {

    if (!snapAUserIdentity || !snapASnoop) return;

    var pubsDiagnosticsText = snapAUserIdentity + snapASnoop;

    new Ajax.Request('http://services.acs.org/paste/', {
    	method: 'post',
    	parameters: {
			paste_data: pubsDiagnosticsText,
			paste_lang: 'HTML',
			paste_private: 'yes',
			api_submit: true,
			mode: 'json'
    	},
    	onSuccess: function(response) {
    		var resultJSON = JSON.parse(response.responseText);
			var pasteURL = 'http://services.acs.org/paste/' + resultJSON.result.id + '/' + resultJSON.result.hash + '/raw/';
			console.log('Snoop and UserIdentities info: ' + pasteURL);
			SnapABug.sendTextToChat('Snoop and UserIdentities info: ' + pasteURL);
    	},
		onException: function(requester, exception){
			console.log(exception);
		},
		onFailure: function(err){
			console.log(err);
		}
    });
};

