var showCarousel = {itemsPerSet:6,totalItems:23,on:1,sets:0,setWidth:((130*6)+(24*6)),
    modify:function(shift){
            if((0>shift&&1!=showCarousel.on)||(0showCarousel.on)){
                    showCarousel.on=showCarousel.on+shift;
                    jQuery(".show-thumbnails").animate({"marginLeft":(((0>shift)?"+":"-")+"="+showCarousel.setWidth+"px")},1500);
                    if(showCarousel.sets==showCarousel.on){jQuery(".next-set div").attr("class","dead");}else{jQuery(".next-set div").attr("class","active");}
                    if(1==showCarousel.on){jQuery(".previous-set div").attr("class","dead");}else{jQuery(".previous-set div").attr("class","active");}
            }
    }
};
showCarousel.sets=Math.ceil(showCarousel.totalItems/showCarousel.itemsPerSet);

// object [DNI] class used to render DNI content w/in DCI pages
DIT.DNIModules = {
        frame: {dimensions:{width:326,height:298}},

        // execute ajax call for DNI module and use jqxhr methods for success / error handeling
        request: function(parameters) {
                var DNIIds = {frame: "#" + parameters.id, status: "#" + parameters.id + "-status", uri: parameters.uri + "?" + parameters.query};
                jQuery(DNIIds.status).html("jquery : iframe src att set");
                jQuery(DNIIds.frame).css({"display": "block", "margin-bottom": "10px"}).attr({src: DNIIds.uri, width: DIT.DNIModules.frame.dimensions.width, height: DIT.DNIModules.frame.dimensions.height});
                // jQuery.ajax({async: true, dataType: "html", type: "GET", url: parameters.uri, data: parameters.query, success: (function(data){DIT.DNIModules.success({html:data,id:parameters.id});}), error: (function(error){DIT.DNIModules.error({code:error.status,key:parameters});})});
        },

        // if ajax success, apply html response to iframe + alter iframe atts
        success: function(data) {
                var DNIIds = {frame: "#"+data.id, status: "#"+data.id+"-status"};
                if (1 < data.html.length) {
                        jQuery(DNIIds.status).html("ajax : yes");
                        jQuery(DNIIds.frame).css({"display": "block", "margin-bottom": "10px"}).attr({width: DIT.DNIModules.frame.dimensions.width, height: DIT.DNIModules.frame.dimensions.height});
                        var frame = document.getElementById(data.id).contentDocument || document.getElementById(data.id).contentWindow; if(frame.document) { frame = frame.document; }
                        frame.open(); frame.writeln(data.html); frame.close();
                }
                else { jQuery(DNIIds.status).html("ajax : yes : < 1"); }
        },

        // if ajax failure, don't render iframe unless IE
        error: function(data) {
                var DNIIds = {frame: "#"+data.key.id, status: "#"+data.key.id+"-status", uri: data.key.uri + "?" + data.key.query};
                jQuery(DNIIds.status).html("ajax : no (" + data.code + ")");
                if (-1 != navigator.userAgent.indexOf("MSIE")) { jQuery(DNIIds.frame).css({"display": "block", "margin-bottom": "10px"}).attr({src: DNIIds.uri, width: DIT.DNIModules.frame.dimensions.width, height: DIT.DNIModules.frame.dimensions.height}); }
        }
};

