body.script:
function hideVideoPlayer(visibility) {
	var player = $('side_video_player');
	if(player != null)
		player.style.visibility = visibility;
}

div.script:
var carousel = new Carousel(
	'carousel-wrapper',
	$$('#carousel-content .slide'),
	$$('a.carousel-control', 'a.carousel-jumper'),
	{
		wheel: false,
		duration: 0.20,
		afterMove: function () {updateSelection();},
		auto: true,
		frequency: 12,
		circular: true
	}
);

function updateSelection() {
	var previous = carousel.previous.getAttribute('id');
	var current = carousel.current.getAttribute('id');

	if(previous == "slide-11") {
		previous = "slide-1";
	}
	if(current == "slide-11") {
		current = "slide-1";
	}

	var prevElement = $$('a[rel='+previous+']')[0];
	var currElement = $$('a[rel='+current+']')[0];

	if(Element.hasClassName(prevElement, carousel.options.selectedClassName)) {
		carousel.controls.invoke('removeClassName', carousel.options.selectedClassName);
		Element.addClassName(currElement, carousel.options.selectedClassName);
	}
};

td.script:
function toggleIndustry(toggler, id) {
	var container = $(id);

	if(container.style.display == "none") {
		new Effect.SlideDown(container, {duration: 0});
	}
	else {
		new Effect.SlideUp(container, {duration: 0});
	}
}

function tth(toggleOn, id, lockOn) {	

	var toggleTab = $('sttab' + id);
	var arrow = $('sstabarr' + id);

	if(toggleOn) {
		toggleTab.style.background = "#2466ba";
		toggleTab.style.color = "#fff";
		arrow.style.display = "inline";

		if(lockOn) {
			toggleTab.onmouseout = "";
		}
	}
	else {
		toggleTab.style.background = "transparent";
		toggleTab.style.color = "#000";
		arrow.style.display = "none";

		if(lockOn) {
			toggleTab.onmouseout = function() {
				toggleTabHighlight(false, id, false);
			}
		}
	}
}
tth(true, 100, true);

div.script:
function EpFv(num) {
	var fvs = $('fvSwitch');
	var eps = $('epSwitch');
	var pos1 = $('epfv1');
	var pos2 = $('epfv2');

	num = parseInt(num);
	if(num > 0) {
		toggleEpFv('fvSwitch');
		pos1.appendChild(fvs);
		pos2.appendChild(eps);
	}
	else {
		toggleEpFv('epSwitch');
	}
}

function toggleEpFv(id) {
	var fvc = $('fvContent');
	var epc = $('epContent');
	var fvs = $('fvSwitch');
	var eps = $('epSwitch');

	if(fvs.id == id) {
		fvs.style.color = "#2466ba";
		eps.style.color = "#222222";
		fvc.style.display = "block";
		epc.style.display = "none";
	}
	else {
		eps.style.color = "#2466ba";
		fvs.style.color = "#222222";
		fvc.style.display = "none";
		epc.style.display = "block";
	}
}

EpFv('0');
