window.fbAsyncInit = function() {
    FB.init({
        appId: '117854138246106',
        status: true,
        cookie: true,
        xfbml: true,
        channelUrl: 'http://aol.sportingnews.com/channel.html'
    });

    // Assign Omniture variables with Facebook Status updates
    FB.getLoginStatus(function(response) {
        if (typeof window.s == "undefined") {
            return;
        }
        if (response.status === 'connected') {
            window.s.prop40 = 'FB:Logged In';
            window.s.eVar40 = 'FB:Logged In';
            window.s.prop41 = 'FB:SN';
            window.s.eVar41 = 'FB:SN';
            window.s.prop42 = response.authResponse.userID;
            window.s.eVar42 = response.authResponse.userID;
        } else if (response.status === 'not_authorized') {
            window.s.prop40 = 'FB:Logged In';
            window.s.eVar40 = 'FB:Logged In';
            window.s.prop41 = 'FB';
            window.s.eVar41 = 'FB';
            window.s.prop42 = response.authResponse.userID;
            window.s.eVar42 = response.authResponse.userID;
        } else {
            window.s.prop40 = 'FB:Not Logged In';
            window.s.eVar40 = 'FB:Not Logged In';
            window.s.prop41 = 'FB:Not Logged In';
            window.s.eVar41 = 'FB:Not Logged In';
        }
     });

     // Add event variable if user logged into Facebook on site
     FB.Event.subscribe('auth.login',
        function(response) {
            if (typeof window.s == "undefined") {
                return;
            }
            window.s.events = window.s.events + ',event40';
        }
    );

};
