div.script:
/**
* User-configurable settings for the recommender
*/
var RecommenderConfiguration = new function ()
{
this.ConfigString = '';
};
RecommenderConfiguration.isConfigured = function ()
{
if ((typeof this.ConfigString == "undefined") || (this.ConfigString == null) || (this.ConfigString.length == 0))
{
if (typeof jslog != "undefined")
{
recommenderDebugMessage("Recommender suppressed, no configuration set.");
}
return false;
}
return true;
}
RecommenderConfiguration.getRecommenderUrl = function ()
{
var ret = this.ConfigString.split(";#")[0];
if ((ret == null) || (ret.length == 0))
{
recommenderInfoMessage("No recommender settings from masterpage setting, using default value for url");
ret = "http://www.automation.siemens.com/search2009/webService/SearchService.asmx/GetSuggestions?query={query}";
}
if (typeof jslog != "undefined")
{
recommenderDebugMessage("Detected recommender xml url: \"" + ret + "\"");
}
return ret;
}
RecommenderConfiguration.getStylesheetUrl = function ()
{
var ret = null;
try
{
ret = this.ConfigString.split(";#")[1];
}
catch (exc)
{
// most probably the xsl url is not set. Ignore exception.
}
if ((ret == null) || (ret.length == 0))
{
if (typeof jslog != "undefined")
{
recommenderInfoMessage("Using builtin default url for xsl...");
}
ret = "/_layouts/_cd/nwa/samples/recommender/render-autocomplete-layer.xsl";
}
if (typeof jslog != "undefined")
{
recommenderDebugMessage("Detected xsl url: \"" + ret + "\"");
}
return ret;
}
/**
* Cached data
*/
var RecommenderCache = new function ()
{
/**
* The Xsl used to transform the recommender responses into the result list
*/
this.xsl = null;
};
RecommenderCache.hasCachedXsl = function()
{
/// Determines if the cache contains a cached xsl transformer
/// true=cache has transformer
return !((typeof this.xsl == "undefined") || (this.xsl == null));
}
function recommenderErrorMessage(msg)
{
if (typeof jslog != "undefined")
{
jslog.error(msg);
}
if ((typeof console != "undefined") && (typeof console.log != "undefined"))
{
console.log("[ERROR] " + msg);
}
}
function recommenderInfoMessage(msg)
{
if (typeof jslog != "undefined")
{
jslog.debug(msg);
}
if ((typeof console != "undefined") && (typeof console.log != "undefined"))
{
console.log("[INFO] " + msg);
}
}
function recommenderDebugMessage(msg)
{
if (typeof jslog != "undefined")
{
jslog.debug(msg);
}
if ((typeof console != "undefined") && (typeof console.log != "undefined"))
{
console.log("[DEBUG] " + msg);
}
}
/**
* Makes the recommender popup visible (if not yet shown)
*/
function showRecommenderPopup()
{
//jQuery(element).is(":visible") // Checks for display:[none|block], ignores visible:[true|false]
if (jQuery("div#site-search-popup").is(":visible"))
{
return;
}
var popup = jQuery("div#site-search-popup");
popup.show();
if (jQuery.browser.mozilla)
{
if (typeof jslog != "undefined")
{
recommenderDebugMessage("Firefox: Dynamically adding search popup keyboard navigation");
}
// Add keyboard navigation for mozilla browser
popup.unbind("keyup.nwasearchPopupKeyUp");
popup.bind("keyup.nwasearchPopupKeyUp", function (event)
{
handleSearchPopupKeyUp(event);
});
}
// Add outside click for close
// TODO: We should detect if a namespaced click is available on the the popup and body. This is not possible with jquery atm.
popup.bind("click.nwasearchOutsideClick1", function (event)  // bind the click using a namespace so that we can remove it if the popup is hidden
{
event.stopPropagation();
});
jQuery("body").bind("click.nwasearchOutsideClick2", function (event) // same for the body
{
//jQuery("input.site-search-input").val(""); // clear
hideRecommenderPopup();
});
}
/**
* Moves the recommender popup to the appropriate position
*/
function repositionRecommenderPopup()
{
jQuery("div#site-search-popup").position({
my: "left top",
at: "left bottom",
of: "input.site-search-input",
offset: "0 4px",
collision: "none"
});
}
/**
* Generates the recommender popup's content by querying the websevice and transforming the result using
* the specified Xsl.
* @returns true=success, false=did not get content or error occured
*/
function fillRecommenderPopup()
{
recommenderInfoMessage("Filling popup");
var query = jQuery("input.site-search-input")[0].value;
var xml = getXml(buildRecommenderQueryUrl(query));
if ((typeof xml == "undefined") || (xml == null) || (xml.childNodes.length == 0))
{
recommenderErrorMessage("The recommender webservice could not be queried, returned xml is empty or unavailable.");
return false;
}
var xsl = RecommenderCache.hasCachedXsl() ? null : getXsl(); // xsl=null means: Use cached transformation
if ((!RecommenderCache.hasCachedXsl()) && ((typeof xsl == "undefined") || (xsl == null) || (xsl.childNodes.length == 0)))   // TODO: Check length
{
recommenderErrorMessage("Recommender queried, Xsl for transformation not available. Giving up.");
return false;
}
var xhtml = xslTransform(xml, xsl, query);
// Generate replacement text if no results found
if ((typeof xhtml == "undefined") || (xhtml == null) || (xhtml.length == 0))
{
xhtml = jQuery("div#site-search-norecommendations")[0].innerHTML;
xhtml = xhtml.replace("{query}", query);
}
try
{
jQuery("div#site-search-popup").html(xhtml);
return true;
}
catch (exc)
{
recommenderErrorMessage("Exception thrown while inserting recommendations into popup: " + exc);
return false;
}
}
/**
* Makes the recommender popup (with search suggestions) disappear, this happens if no character is in the search box
*/
function hideRecommenderPopup()
{
// Remove 'outside click' handler for popup
var popup = jQuery("div#site-search-popup");
popup.unbind("click.nwasearchOutsideClick1");
jQuery("body").unbind("click.nwasearchOutsideClick2");
if (!popup.is(":visible"))
{
return;
}
popup.hide();
}
/**
* Handles the keyboard navigation for the search popup
* @param event This event object contains the key code
*/
function handleSearchPopupKeyUp(event)
{
recommenderDebugMessage("KeyCode=" + event.keyCode);
if (event.keyCode == '13')
{
// enter pressed --> search
var suggestText = jQuery("div#site-search-popup ul li.site-search-selectedelement").text();
if ((suggestText != null) && (suggestText.length > 0))
{
nwaRecommenderExecuteSearch(suggestText);
}
}
else if (event.keyCode == '27')
{
// Escape pressed --> Close, go back to textbox
hideRecommenderPopup();
jQuery("input.site-search-input").focus();
}
else if (event.keyCode == '38')
{
// Cursor-up pressed
var popup = jQuery("div#site-search-popup");
var dx = getSuggestionListSelectedIndex();
if (dx > 0)
{
setSuggestionListSelectedIndex(dx - 1);
event.stopPropagation();
}
}
else if (event.keyCode == '40')
{
// Cursor-down pressed
var dx = getSuggestionListSelectedIndex();
var max = jQuery("div#site-search-popup ul li").length;
if (dx < (max - 1))
{
setSuggestionListSelectedIndex(dx + 1);
event.stopPropagation();
}
}
else
{
// set focus back to search box
jQuery("input.site-search-input").focus();
event.stopPropagation();
}
}
/**
* Gets the first element in the search suggest list that uses the class css "site-search-selectedelement". Returns its number.
* @return -1 if nothing found, a number else
*/
function getSuggestionListSelectedIndex()
{
return jQuery("div#site-search-popup ul li.site-search-selectedelement").index();
}
/**
* Moves or sets the selection of a single element in an UL by adding the CSS class "site-search-selectedelement"
* @param index Index of the element to be selected. "-1" means: Deselect all.
*/
function setSuggestionListSelectedIndex(index)
{
// Unselect all
jQuery("div#site-search-popup ul li.site-search-selectedelement").removeClass("site-search-selectedelement");
if (index >= 0)
{
// Select element
var element = jQuery("div#site-search-popup ul li:eq(" + index + ")");
if ((element != null) && (element.length > 0))
{
element.addClass("site-search-selectedelement");
}
else
{
recommenderErrorMessage("Index " + index + " not found.");
}
}
}
/**
* Reacts on key releases in search box, used for processing "Enter" key
* @param event This event object contains the key code
*/
function handleSearchBoxKeyDown(event)
{
if (event.keyCode == '13')
{
// Submit action
event.preventDefault();
PortalSearchSubmit("go", null, jQuery("input.site-search-input").attr("id"));  // TODO: Get from button
}
}
/**
* Reacts on keypresses in the search box, builds recommendations
* @param event This event object contains the key code
*/
function handleSearchBoxKeyUp(event)
{
if (event.keyCode == '13')
{
// enter key is processed in keyup
try
{
event.stopPropagation();
event.preventDefault();
}
catch (exc)
{
// exception of no use here.
}
}
else if (event.keyCode == '27')
{
// Escape pressed
jQuery("input.site-search-input").val(""); // clear textbox
hideRecommenderPopup();
}
else if (event.keyCode == '40')
{
if (RecommenderConfiguration.isConfigured())
{
// Cursor-down pressed
var popup = jQuery("div#site-search-popup");
if (popup.is(":visible"))
{
popup.focus();
// select first element
var suggestCount = jQuery("div#site-search-popup ul li").length;
if (suggestCount > 0)
{
setSuggestionListSelectedIndex(0);
}
}
}
}
else
{
if (RecommenderConfiguration.isConfigured())
{
// Other KeyPress, might be backspace or printable key
if (jQuery("input.site-search-input")[0].value.length > 0)
{
if (fillRecommenderPopup())
{
showRecommenderPopup();
repositionRecommenderPopup();
// Developer note: Sometimes the JQuery.Position method calculates an incorrect position.
//                 Nevertheless the calculation will be corrected on the second call.
repositionRecommenderPopup();
}
else
{
//hideRecommenderPopup();
}
}
else
{
hideRecommenderPopup();
}
}
}
}
