/**
* Browser independent method for fetching an xsl
*/
function getXml(xmlurl)
{
var xmlDoc = null;
try
{
if (jQuery.browser.msie)
{
//Code for Internet Explorer
try
{
xmlDoc = new ActiveXObject("MSXML2.FreeThreadedDomDocument");
xmlDoc.async = "false";
xmlDoc.load(xmlurl);
}
catch (ex)
{
jslog.error("Error loading xml or xsl file \"" + xmlurl + "\": " + ex.message);
return null;
}
}
else
{
//Code for Firefox
jQuery.ajax({
type: "GET",
url: xmlurl,
dataType: "xml",
async: false,
success: function (resultxml)
{
recommenderInfoMessage("Recommender: Got xml or xsl file \"" + xmlurl + "\"");
xmlDoc = resultxml;
},
error: function (jqXHR, textStatus, errorThrown)
{
if (typeof (errorThrown.message) == "undefined")
{
recommenderErrorMessage("Error loading xml or xsl file \""+xmlurl+"\": \"" + errorThrown + "\"");
}
else
{
recommenderErrorMessage("Error loading xml or xsl file \"" + xmlurl + "\": \"" + errorThrown.message + "\"");
}
xmlDoc = null;
}
});
}
}
catch (exc)
{
recommenderErrorMessage("Failed to get xml file \"" + xmlurl + "\". Exception=" + exc);
}
return xmlDoc;
}
/**
* Gets the Xsl that is necessary to transform the recommender result into html
*/
function getXsl()
{
return getXml(RecommenderConfiguration.getStylesheetUrl());
}
/**
* Transforms the given xml document using the given style sheet
* @param xml The xml document, type is either oXMLDocument (Firefox) or MSXML2.FreeThreadedDomDocument (IE)
*/
function xslTransform(xml, xsl, query)
{
/// 
/// Transforms the given xml document using the given style sheet
/// 
/// The xml obtained from the recommender service
/// The xsl used to transform the xml. If null the cached transformation will be used
/// The query from the search box, used for highlighting
/// Transformed xml as ?oXMLDocument?
try
{
if (window.ActiveXObject) // IE Only
{
var xSLTProc = null;
if (xsl != null)
{
// create new processor
var xSLTCompiled = new ActiveXObject("MSXML2.XSLTemplate");
xSLTCompiled.stylesheet = xsl.documentElement;
var xSLTProc = xSLTCompiled.createProcessor();
xSLTProc.addParameter("selectedText", query);
RecommenderCache.xsl = xSLTProc;
}
else
{
// use cached processor
xSLTProc = RecommenderCache.xsl;
xSLTProc.addParameter("selectedText", query);
}
xSLTProc.input = xml;
xSLTProc.transform();
var ret = xSLTProc.output;
if ((typeof ret == "undefined") || (ret == null))
{
ret = "";
}
recommenderInfoMessage("Transformed recommender result, length=" + ret.length);
return ret;
}
else if (document.implementation && document.implementation.createDocument) // other than IE
{
var xsltProcessor = null;
if (xsl!=null)
{
// create new processor
xsltProcessor = new XSLTProcessor();
xsltProcessor.importStylesheet(xsl);
RecommenderCache.xsl = xsltProcessor;
}
else
{
xsltProcessor = RecommenderCache.xsl;
}
xsltProcessor.setParameter(null,"selectedText", query);
var resultDocument = xsltProcessor.transformToFragment(xml, document);
recommenderInfoMessage("Transformed recommender result, length=" + resultDocument.childNodes.length);
return resultDocument;
}
}
catch (ex)
{
recommenderErrorMessage("Unable to transform: " + ex.message);
}
}
/**
* Used by the recommender xsl. Inserts the given text into the search box
*/
function setSearchTermFromSuggestions(text)
