* @param text The text to be searched for
*/
function nwaRecommenderExecuteSearch(text)
{
/// Inserts the text from the suggestions list into the search text box and executes the search.
/// The text to be searched for
setSearchTermFromSuggestions(text);
PortalSearchSubmit("go", null, jQuery("input.site-search-input").attr("id"));  // TODO: Get from button
}
