/**
* Cache for the properties needed in the ReUse Cockpit Ajax Requests
*/
function RcpPageProperties()
{
this.currentPageUrl = "";  // Absolute url of the current page
this.matchedHQPageUrl = "";  // Matched HQ Url for the current page
this.objectHistoryId = ""; // Internal database id needed for access to the source item's unique id
}
RcpPageProperties.prototype.isValid = function()
{
if ((this.currentPageUrl==null) || (this.currentPageUrl.length==0))
{
return false;
}
if ((this.matchedHQPageUrl==null) || (this.matchedHQPageUrl.length==0))
{
return false;
}
return true;
};
var _rcpPageProperties = new RcpPageProperties();
var rcpgpCurrentWebUrl = 'http://www.siemens.de/func/errors';
var rcpgpItemGuid = "";
/**
* ReUse Cockpit GetPage: Starts the "Update" dialog
*/
function rcpgpShowDialog(siteUrl, webUrl, pageUrl)
{
jQuery("#rcpGetPageFromHQDialog").dialog(
{
title: "Content ReUse",
dialogClass: "rcpUpdatePageDialog",
width: 640,
height: 220,
buttons:
{
"Update": function ()
{
// Handle "Update" click
_rcpgpStartUpdate();
},
"Close": function () { jQuery(this).dialog("close"); }
},
open: function (event, ui)
{
_rcpLogDebug("Opening matcher dialog");
// Init dialog content
jQuery("#rcpgpHQPageInfo").hide();
jQuery("#rcpgpHQPageError").hide();
jQuery("#rcpgpCopyProgress").hide();
jQuery("#rcpgpCopyResult").hide();
jQuery("#rcpgpCopyResultFailed").hide();
_rcpLogDebug("Disabling button");
// Disable "Update" button
_rcpDisableDialogButton("rcpUpdatePageDialog", "Update", false);
_rcpLogDebug("Starting HQ match");
rcpgpStartHQPageMatch(siteUrl, webUrl, pageUrl);
}
});
}
/**
* Calls the HQ page matcher via Ajax
*/
function rcpgpStartHQPageMatch(siteUrl, webUrl, pageUrl)
{
//setTimeout("_rcpgpTestStartHQPageMatch(false)", 2000);
_rcpLogDebug("Starting page match.");
_rcpLogDebug("Showing #rcpgpWaitGettingHQPage");
jQuery("#rcpgpWaitGettingHQPage").show();
jQuery.ajax({
cache: false,
type: "POST",
url: "/_layouts/Siemens.Industry.ReUseCockpit/JQueryRequest.ashx",
data:
{
Method: "FindSourceUrlForPage",
//CurrentWebUrl: rcpgpCurrentWebUrl
CurrentSiteUrl: siteUrl, // needed by database
CurrentWebUrl: webUrl,
CurrentPageUrl: pageUrl
},
dataType: "json",
//statuscode:
//{
//    404: function()
//    {
//
//    }
//},
success: function (envelope)
{
_rcpLogDebug("Page match finished.");
jQuery("#rcpgpWaitGettingHQPage").hide();
// Disable "Update" button
if (envelope.Error == null)
{
var targetUrl = envelope.Result.InSiteRelativeUrl;
var title = envelope.Result.Title;
var objectHistoryId = envelope.Result.ObjectHistoryId;
if ((typeof targetUrl == "undefined") || (targetUrl == null) || (targetUrl.length == 0))
{
// Nothing found
//jQuery("#rcpgpHQPageError").fadeIn(300);
jQuery("#rcpgpHQPageError").show();
jQuery("#rcpgpMatchErrorImage").attr("title", "This page has no matching HQ item.");
// Store results for "update" operation
_rcpPageProperties.matchedHQPageUrl = "";
_rcpPageProperties.currentPageUrl = "";
_rcpPageProperties.objectHistoryId = "";
}
else
{
_rcpLogDebug("Found hq page, enabling update button.");
_rcpDisableDialogButton("rcpUpdatePageDialog", "Update", true);
// Match found
if ((typeof title == "undefined") || (title == null) || (title.length == 0))
{
title = "(Click to open)";
}
//jQuery("#rcpgpHQPageInfo").fadeIn(300);
jQuery("#rcpgpHQPageInfo").show();
jQuery("#rcpgpHQPageLink").attr("href", targetUrl);
jQuery("#rcpgpHQPageLink").html(title);
// Store results for "update" operation
_rcpPageProperties.matchedHQPageUrl = targetUrl;
_rcpPageProperties.currentPageUrl = pageUrl;
_rcpPageProperties.objectHistoryId = objectHistoryId;
}
}
else
{
_rcpLogDebug("Showing page match error.");
//jQuery("#rcpgpHQPageError").fadeIn(300);
jQuery("#rcpgpHQPageError").show();
jQuery("#rcpgpMatchErrorImage").attr("title", envelope.Error.Detail);
_rcpLogDebug("Page match error is: " + envelope.Error.Detail);
// Store results for "update" operation
_rcpPageProperties.matchedHQPageUrl = "";
_rcpPageProperties.currentPageUrl = "";
_rcpPageProperties.objectHistoryId = "";
//                    var error = jQuery(".Error", tr)[0];
//                    SetAttribute(error, "title", envelope.Error.Message);
//                    SetAttribute(error, "detail", envelope.Error.Detail);
//                    SetErrorTR(tr);
}
},
error: function (XMLHttpRequest, textStatus, errorThrown)
{
_rcpLogDebug("Page match finished, error.");
jQuery("#rcpgpWaitGettingHQPage").hide();
//jQuery("#rcpgpHQPageError").fadeIn(300);
jQuery("#rcpgpHQPageError").show();
jQuery("#rcpgpMatchErrorImage").attr("title", errorThrown != null ? errorThrown.toString() : "(no details available)");
// Store results for "update" operation
_rcpPageProperties.matchedHQPageUrl = "";
_rcpPageProperties.currentPageUrl = "";
_rcpPageProperties.objectHistoryId = "";
//                var error = jQuery(".Error", tr)[0];
//                SetAttribute(error, "title", textStatus);
//                SetAttribute(error, "detail", errorThrown != null ? errorThrown.toString() : null);
//                SetErrorTR(tr);
}
});
}
/**
* Calls the page update mechanism via Ajax
*/
function _rcpgpStartUpdate()
{
if (_rcpPageProperties.isValid())
{
var copyUrl = _rcpGenerateCopyUrl(_rcpPageProperties.matchedHQPageUrl, _rcpPageProperties.currentPageUrl);
_rcpSendCopyRequest(copyUrl);  // Reaction to copy result is event-driven
}
else
{
alert("There is no matching HQ page, update not possible.");
}
}
/**
* Testing
*/
function _rcpgpTestStartHQPageMatch(success)
{
jQuery("#rcpgpWaitGettingHQPage").hide();
if (success)
{
