function _rcpgpTestStartUpdate(success)
{
jQuery("#rcpgpCopyProgress").hide();
if (success)
{
//jQuery("#rcpgpCopyResult").fadeIn(300);
jQuery("#rcpgpCopyResult").show();
}
else
{
//jQuery("#rcpgpCopyResultFailed").fadeIn(300);
jQuery("#rcpgpCopyResultFailed").show();
jQuery("#rcpgpCopyErrorImage").attr("title", "A test error message");
}
}

