window.addEvent('domready', function() {
  var slider = new Slideshow($('slideshow'), {
    onSlideUpdated: function(i) {

      var links = $$('#remote a');
      links.removeClass('selected');

      links.each(function(a, index) {
        if(i == index) { a.addClass('selected'); }
      });
    }
  });

  $$('#remote a').each(function(a, index) {
    a.addEvent('click', function(e) {
      e.stop();
      slider.goTo(index);
    });
  });

  new Placeholder($('search-field'), 'search site', {
    onFocus: function(elm) { 
      elm.removeClass('search-placeholder');                      
    },
    onSupported: function(elm) { 
      elm.removeClass('search-placeholder');                      
    },
    onBlur: function(elm) { 
      if(elm.get('value') == '' || elm.get('value') == this.text)
        elm.addClass('search-placeholder'); 
    }
  });

});

