	jQuery(function($) {

		var $bgad = $('adservercode');
		if ($bgad.length && $bgad.attr('creativead') != undefined) {

			//Tracking pixel
			if ($bgad.attr('thirdpartyimp') != undefined) {
				var dc_tracking_pixel = new Image();
				dc_tracking_pixel.src = $bgad.attr('thirdpartyimp');
			}

			//get the background url
			var dc_bg_url = $bgad.attr('creativead');
			var db_bg_color = ($bgad.attr('bgcolor') != undefined) ? $bgad.attr('bgcolor') : '';
			$('body').css('background', db_bg_color + " url('" + dc_bg_url + "') no-repeat fixed top center");

			if ($bgad.attr('clickthrough') != undefined) {

				//for mouse-over pointer on body
				$('div#wrapper').css('cursor', 'pointer');
				$('div#wrapper > *').hover(function() {
					$('div#wrapper').css('cursor', '');
				}, function() {
					$('div#wrapper').css('cursor', 'pointer');
				});

				var dc_bg_clickthrough = $bgad.attr('clickthrough');
				$('body').click(function(e) {
					var event_element = e.target ? e.target : e.srcElement;
					event_element = $(event_element);

					if (event_element.is( "div#wrapper" )) {
						window.open(dc_bg_clickthrough);
					}
				});
			}
		}
	});
