$(document).ready(function(){

	slide = $(".slider-inner .slide:visible");
	var $count = 5;
	var $actual = 0;
	var $dir = "forward"; //dont change
	var $delay = 6000;

	function slideTimer(){
		slide = $(".slider-inner .slide:visible");
		if($actual <= $count && $dir == "forward"){
			slideNext();
			$actual++;
			if($actual == $count){
				$dir = "backward";
			}
		}
		if($actual <= $count && $dir == "backward"){
			slidePrev();
			$actual--
			if($actual == 0){
				$dir = "forward";
			}
		}
		return setTimeout(slideTimer, $delay);
	}

	function slideNext(){
		if(slide.next().hasClass('slide')){
			slide = $(".slider-inner .slide:visible");
			slide.hide();
			slide.next('.slide').fadeIn();
		}
	}

	function slidePrev(){
		if(slide.prev().hasClass('slide')){
			slide = $(".slider-inner .slide:visible");
			slide.hide();
			slide.prev('.slide').fadeIn();
		}
	}

	$("#prev").click(function(){
		slidePrev();
		$actual--;
	});
	$("#next").click(function(){
		slideNext();
		$actual++;
	});

	setTimeout(slideTimer, $delay);

});
